package main

import (
	"fmt"
	"os"
	"runtime"
)

const (
	// PREFIX1 and PREFIX2 should have the same rolling sum and be 2028 bytes long
	PREFIX1 = ""
	PREFIX2 = ""
)

func main() {
	runtime.GOMAXPROCS(runtime.NumCPU())

	for i := uint64(0); i < 27; i++ {
		t := uint64(1<<i) + i
		if decode_seed(encode_seed(t)) != t {
			panic("Seeds don't match")
		}
	}

	prefix1 := []byte(PREFIX1)
	prefix2 := []byte(PREFIX2)

	result, ok := Run(prefix1, prefix2)
	if !ok {
		return
	}

	file1, err := os.Create("coll1.bin")
	if err != nil {
		fmt.Println("Unable to create coll1.bin:", err)
		return
	}
	file1.Write(prefix1)
	file1.Write(rollsum_expand(result.Seed1))
	file1.Close()

	file2, err := os.Create("coll2.bin")
	if err != nil {
		fmt.Println("Unable to create coll2.bin:", err)
		return
	}
	file2.Write(prefix2)
	file2.Write(rollsum_expand(result.Seed2))
	file2.Close()
}
